    private void paypalBtnMouseClicked(java.awt.event.MouseEvent evt) {//GEN-FIRST:event_paypalBtnMouseClicked
		try {
			HtmlBrowser.URLDisplayer.getDefault().showURLExternal(new URL("https://www.paypal.com/cgi-bin/webscr?cmd=_s-xclick&hosted_button_id=926F5XBCTK2LQ&source=url"));
		} catch (MalformedURLException ex) {
			Exceptions.printStackTrace(ex);
		}
    }//GEN-LAST:event_paypalBtnMouseClicked

    private void patronBtnMouseClicked(java.awt.event.MouseEvent evt) {//GEN-FIRST:event_patronBtnMouseClicked
        try {
			HtmlBrowser.URLDisplayer.getDefault().showURLExternal(new URL("https://www.patreon.com/funfried"));
		} catch (MalformedURLException ex) {
			Exceptions.printStackTrace(ex);
		}
    }//GEN-LAST:event_patronBtnMouseClicked

    private void buyMeACoffeeBtnMouseClicked(java.awt.event.MouseEvent evt) {//GEN-FIRST:event_buyMeACoffeeBtnMouseClicked
        try {
			HtmlBrowser.URLDisplayer.getDefault().showURLExternal(new URL("https://www.buymeacoffee.com/funfried"));
		} catch (MalformedURLException ex) {
			Exceptions.printStackTrace(ex);
		}
    }//GEN-LAST:event_buyMeACoffeeBtnMouseClicked

    private void sayThanksBtnMouseClicked(java.awt.event.MouseEvent evt) {//GEN-FIRST:event_sayThanksBtnMouseClicked
        try {
			HtmlBrowser.URLDisplayer.getDefault().showURLExternal(new URL("https://saythanks.io/to/funfried"));
		} catch (MalformedURLException ex) {
			Exceptions.printStackTrace(ex);
		}
    }//GEN-LAST:event_sayThanksBtnMouseClicked

